import React, {Component} from 'react';
import { GraphQLClient } from 'graphql-request'

const gql = new GraphQLClient("http://localhost:4000/graphql", { headers: {} })
export class MainPage extends Component{
    componentDidMount() {

        gql.request(`query getI{
        getIngridients{
            title,
                description

        }}`, {id:5}).then(data=> this.setState({ingridientsList: data.getIngridients}))

        let promise = fetch('http://localhost:4000/user_form', {
            method:'post'
        });
        promise.then((response) => {
            console.log(response.json())
        })
    }
    constructor(props){
        super(props);
        this.state = {
            ingridientsList:[],
            recipeName: null,
            recipeDescription: null

        }
    }

    handleClick = (e) => {
        const {
            recipeDescription,recipeName
        } = this.state;
        console.log(recipeDescription, recipeName)
    }

    handleDescription = (e) => {

        this.setState({recipeDescription: e.target.value})
    }

    handleName = (e) => {
        this.setState({recipeName: e.target.value})
    }

    render(){
        console.log(this.state)
        return(
           <div className='fpage'>
               <section className='group_header'>
                   <h1> Мои рецепты</h1>
               </section>
               <div>
                   <select>
                       {
                           this.state.ingridientsList.map(( item, index) => {
                              return (
                                  <option key={index} value={item.id}>{item.title}</option>
                              )
                           })
                       }
                   </select>
                        <input onClick={this.handleName}/>
                       <textarea onChange={this.handleDescription} placeholder="название рецепта"></textarea>
                       <button onClick={this.handleClick}>Отправить</button>


               </div>
           </div>

        )
    }
}
