import React, {Component} from 'react';
import { GraphQLClient } from 'graphql-request';
// import axios from 'axios';

const gql = new GraphQLClient("http://localhost:4000/graphql", { headers: {} });
export class MainPage extends Component{
    componentDidMount() {

        gql.request(`query getI{
        getIngridients{
                id,
                title,
                description

        }}`, {}).then(data=> this.setState({ingridientsList: data.getIngridients}));

        let promise = fetch('http://localhost:4000/user_form', {
            method:'post'
        });

    };

    constructor(props){
        super(props);
        this.state = {
            ingridientsList:[],
            recipeName: null,
            recipeDescription: null,
            RecipeIngridients: []
        }
    };

    handleSubmit = (e) => {
            e.preventDefault();
        const {
            recipeDescription,recipeName,
        } = this.state;
        gql.request(`mutation  createRecipe(
            $title:String!,  $description:String!){
            createRecipe(
                title:$title,description:$description
            )
            {
                title, description
            }}`, {title:recipeName, description:recipeDescription})
            // .then(() => this.props.history.push('/'))
    };

    handleDescription = (e) => {

        this.setState({recipeDescription: e.target.value})
    };

    handleName = (e) => {

        this.setState({recipeName: e.target.value})
    };

//     handleAdd = (e) => {
// [...ingridientsList,{}]
//         this.setState(state,{ingridientsList: e.target.value })
//
//     };


    // componentDidMount() {
    //     axios.get(`http://localhost:4000`)
    //         .then(res => {
    //             console.log('ok')
    //             const persons = res.data;
    //             this.setState({ persons });
    //         })
    // }
    //
    // handleSubmit = event => {
    //     event.preventDefault();
    //
    //     const params = { recipeName:this.state.recipeName,
    //         recipeDescription:this.state.recipeDescription
    //     }
    //
    //     axios.post(`http://localhost:4000`)
    //         .then(res => {
    //             console.log(res);
    //             console.log(res.data);
    //         })
    // };


    render(){
        console.log(this.state, this.props)
        return(
           <div className='fpage'>
               <section className='group_header'>
                   <h1> Мои рецепты</h1>
               </section>
               <div>
                   <input type="number" />
                   <select>
                       {this.state.ingridientsList ? this.state.ingridientsList.map(ing => <option value={`${ing.id}`}>{ing.title}</option>) : 'loading'}
                   </select>
                   <br/>
                       <input type="number" />
                           <select>
                               {this.state.ingridientsList ? this.state.ingridientsList.map(ing => <option value={`${ing.id}`}>{ing.title}</option>) : 'loading'}
                           </select>


                           <form onSubmit={this.handleSubmit}>
                       <input onChange={this.handleName} placeholder="Название"/><br/>
                       <textarea onChange={this.handleDescription} placeholder="описание рецепта"></textarea>
                       <button onSubmit={this.handleAdd}>Добавить ингридиенты</button>
                       <button onSubmit={this.handleSubmit}>Отправить</button>
                   </form>
               </div>
           </div>

        )
    };
}
