import React, {Component} from 'react';
import { GraphQLClient } from 'graphql-request';
import { Table, Button, Navbar } from 'react-bootstrap';

const gql = new GraphQLClient("http://localhost:4000/graphql", { headers: {} });
export class MainPage extends Component{
    componentDidMount() {

        gql.request(`query getI{
        getIngridients{
            title,
                description

        }}`, {}).then(data=> this.setState({ingridientsList: data.getIngridients}));

        let promise = fetch('http://localhost:4000/user_form', {
            method:'post'
        });
        promise.then((response) => {
            console.log(response.json())
        })
    };

    constructor(props){
        super(props);
        this.state = {
            ingridientsList:[],
            recipeName: null,
            recipeDescription: null,
        }
    };

    handleClick = (e) => {
        const {
            recipeDescription,recipeName,
        } = this.state;
        gql.request(`mutation  createRecipe(
            $title:String!,  $description:String!){
            createRecipe(
                title:$title,description:$description
            )
            {
                title, description
            }}`, {title:recipeName, description:recipeDescription})
            .then(() => this.props.history.push('/'))
    };

    handleDescription = (e) => {

        this.setState({recipeDescription: e.target.value})
    };

    handleName = (e) => {

        this.setState({recipeName: e.target.value})
    };


    render(){
        console.log(this.state, this.props)
        return(
           <div className='fpage'>
               <section className='group_header'>
                   <h1> Мои рецепты</h1>
               </section>
               <div>
                   <select>
                       {
                           this.state.ingridientsList.map(( item, index) => {
                              return (
                                  <option key={index} value={item.id}>{item.title}</option>
                              )
                           })
                       }
                   </select>
                        <input onChange={this.handleName} placeholder="Название"/>
                       <textarea onChange={this.handleDescription} placeholder="описание рецепта"></textarea><br/>
                       <button variant="dark"  onClick={this.handleClick}>Отправить</button>
               </div>
           </div>

        )
    };
}
