import React, {Component} from 'react';
import { GraphQLClient } from 'graphql-request'

const gql = new GraphQLClient("http://localhost:4000/graphql", { headers: {} })
export class MainPage extends Component{
    componentDidMount() {

        gql.request(`query getI{
        getIngridients{
            title,
                description

        }}`, {id:5}).then(data=> this.setState({ingridientsList: data.getIngridients}))

        let promise = fetch('http://localhost:4000/user_form', {
            method:'post'
        });
        promise.then((response) => {
            console.log(response.json())
        })
    }
    constructor(props){
        super(props);
        this.state = {
            ingridientsList:[]
        }
    }

    render(){
        console.log(this.state.ingridientsList)
        return(
           <div className='fpage'>
               <section className='group_header'>
                   <h1> Мои рецепты</h1>
               </section>
               <div>
                   <select>
                       {
                           this.state.ingridientsList.map(( item, index) => {
                              return (
                                  <option key={index} value={item.id}>{item.title}</option>
                              )
                           })
                       }
                   </select>
                   <form>
                       <textarea placeholder="название рецепта"></textarea>
                   </form>
               </div>
           </div>

        )
    }
}
